mapper.sql:
        FROM
			code a
		    LEFT JOIN codegroup b ON b.cogrSeq = a.codeGroup_cogrSeq
        WHERE 1=1
        AND a.codeUseFlag = #{shUseFlag}
        AND a.codeDelFlag = #{shDelFlag}


			AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.codeGroup_cogrSeq = #{shValue}
            AND b.cogrName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeSeq = #{shValue}
            AND a.codeNum LIKE CONCAT('%',#{shValue},'%')
            AND a.codeName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeNameEng LIKE CONCAT('%',#{shValue},'%')
