mapper.sql:
        FROM user a
        WHERE 1=1
        AND a.userDelFlag = #{shDelFlag}
        AND a.userSecession = #{shSecession}


			AND a.userRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.userModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.userName LIKE CONCAT('%',#{shValue},'%')
            AND a.userId LIKE CONCAT('%',#{shValue},'%')
            AND a.userEmail LIKE CONCAT('%',#{shValue},'%')
            AND a.userPhone LIKE CONCAT('%',#{shValue},'%')
            AND a.userAddr LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE user
SET 
	userPassword = #{userPassword}
WHERE
	userSeq = #{userSeq}
