mapper.sql:
        FROM pet a
        	LEFT JOIN user b ON a.user_userSeq = b.userSeq
        WHERE 1=1
        AND a.petDelFlag = #{shDelFlag}
        AND a.petVaccinationFlag = #{shVaccineFlag}
        AND a.petNeuteringFlag = #{shNeuterFlag}
        AND a.petType = #{shType}
        AND a.petVarieties = #{shVarieties}

        	AND a.petWeight BETWEEN #{shMinWeight} AND #{shMaxWeight}



			AND a.petRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.petModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.petName LIKE CONCAT('%',#{shValue},'%')
            AND b.userName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO personality(
	persDiscription,
	pet_petSeq
) VALUE(
	#{persDiscription},
	#{petSeq}
)

	SELECT last_insert_id()

INSERT INTO disease(
	diseDiscription,
	pet_petSeq
) VALUE(
	#{diseDiscription},
	#{petSeq}
)

	SELECT last_insert_id()
