mapper.sql:
        FROM product a
        WHERE 1=1
        	AND prodType = #{prodType}
        	AND a.prodName LIKE CONCAT('%',#{shValue},'%')
		        AND a.feedDelFlag = #{shDelFlag}


			        AND a.feedSalaryAge = #{shFeedSalaryAge}
			        AND a.feedType = #{shFeedType}
			        AND a.feedEtc = #{shFeedEtc}
			        AND a.feedSize = #{shFeedSize}
			        AND a.feedBrand = #{shFeedBrand}
			        AND a.feedIngredient = #{shFeedIngredient}
			        AND a.prodTeethFlag = #{prodTeethFlag}
		        	AND a.prodTearsFlag = #{prodTearsFlag}
		        	AND a.prodBrainFlag = #{prodBrainFlag}
		        	AND a.prodImmunityFlag = #{prodImmunityFlag}
		        	AND a.prodBoneFlag = #{prodBoneFlag}
		        	AND a.prodStressFlag = #{prodStressFlag}
		        	AND a.prodKidneyFlag = #{prodKidneyFlag}
		        	AND a.prodHeartFlag = #{prodHeartFlag}
		        	AND a.prodAllergyFlag = #{prodAllergyFlag}
		        	AND a.prodPregnancyFlag = #{prodPregnancyFlag}
		        	AND a.prodIntestineFlag = #{prodIntestineFlag}
		        	AND a.prodNeuteringFlag = #{prodNeuteringFlag}
		        	AND a.prodWeightFlag = #{prodWeightFlag}
		        	AND a.prodSkinFlag = #{prodSkinFlag}



			        AND a.snackType = #{shSnackType}
			        AND a.SnackSalaryAge = #{shSnackSalaryAge}
			        AND a.snackMaterial = #{shSnackMaterial}
			        AND a.snackBrand = #{shSnackBrand}
			        AND a.snackIngredient = #{shSnackIngredient}
