mapper.sql:
        FROM product a
        LEFT JOIN (SELECT
        		GROUP_CONCAT(funcName) AS funcName,
        		product_prodSeq
        	FROM
        		func
        	GROUP BY product_prodSeq) AS b ON a.prodSeq = b.product_prodSeq
        LEFT JOIN feedinfo c ON c.product_prodSeq = a.prodSeq AND infoMain = 1
        WHERE 1=1
        	AND prodType = #{prodType}
        	AND a.prodName LIKE CONCAT('%',#{shValue},'%')
		        AND a.prodDelFlag = #{shDelFlag}
		        AND a.prodPetType = #{prodPetType}


			        AND a.feedSalaryAge = #{shFeedSalaryAge}
			        AND a.feedType = #{shFeedType}
			        AND a.feedEtc = #{shFeedEtc}
			        AND a.feedSize = #{shFeedSize}
			        AND a.feedBrand = #{shFeedBrand}
			        AND a.feedIngredient = #{shFeedIngredient}


			        		AND FIND_IN_SET(#{func}, funcName) > 0





			        AND a.feedType = #{shFeedType}
			        AND a.snckFeature = #{shSnckFeature}
			        AND a.feedIngredient = #{shFeedIngredient}
			        AND a.feedBrand = #{shFeedBrand}


			        		AND FIND_IN_SET(#{func}, funcName) > 0





			        AND a.feedType = #{shFeedType}
			        AND a.snckFeature = #{shSnckFeature}
			        AND a.feedIngredient = #{shFeedIngredient}
			        AND a.feedBrand = #{shFeedBrand}



					AND a.prodRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.prodModDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO func(
	funcName,
	funcMain,
	product_prodSeq
) VALUE (
	#{funcName},
	#{funcMain},
	#{prodSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE func
SET
	funcName = #{funcName}
WHERE
	funcSeq = #{funcSeq}

mapper.delete:
DELETE FROM product
WHERE prodSeq IN

	#{item}

mapper.sql:
	FROM product a
		LEFT JOIN feedinfo b ON a.prodSeq = b.product_prodSeq AND infoMain = 1
		LEFT JOIN func c ON a.prodSeq = c.product_prodSeq AND funcMain = 1
	WHERE 1=1
		AND a.prodDelFlag = 0
		AND a.prodType = #{prodType}
		AND (b.infoPrice * (100 - b.infoDiscount) / 100) BETWEEN #{shMinPrice} AND #{shMaxPrice}
		AND a.feedSalaryAge = #{shFeedSalaryAge}
		AND a.feedType = #{shFeedType}
		AND a.prodPetType = #{prodPetType}


        		AND FIND_IN_SET(#{func}, (SELECT GROUP_CONCAT(funcName) FROM func WHERE a.prodSeq = product_prodSeq)) > 0


        AND a.feedEtc = #{shFeedEtc}

			AND NOT EXISTS (SELECT 1 FROM feedinfo WHERE a.prodSeq = product_prodSeq AND infoWeight >= 1)
			AND NOT EXISTS (SELECT 1 FROM feedinfo WHERE a.prodSeq = product_prodSeq AND infoWeight NOT BETWEEN 1 AND 2)
			AND NOT EXISTS (SELECT 1 FROM feedinfo WHERE a.prodSeq = product_prodSeq AND infoWeight NOT BETWEEN 2 AND 5)
			AND NOT EXISTS (SELECT 1 FROM feedinfo WHERE a.prodSeq = product_prodSeq AND infoWeight NOT BETWEEN 5 AND 10)
			AND NOT EXISTS (SELECT 1 FROM feedinfo WHERE a.prodSeq = product_prodSeq AND infoWeight NOT BETWEEN 10 AND 15)
			AND NOT EXISTS (SELECT 1 FROM feedinfo WHERE a.prodSeq = product_prodSeq AND infoWeight < 15)


			AND a.feedBrand IN

				#{item}



			AND a.feedIngredient IN

				#{item}


        AND a.feedSize = #{shFeedSize}
        AND a.snckFeature = #{shSnckFeature}

mapper.select:
SELECT a.prodSeq, a.prodName, a.prodScore, a.feedBrand, b.infoPrice, b.infoWeight, b.infoDiscount, c.funcName, (SELECT path FROM image WHERE a.prodSeq = pseq AND delNy = 0 AND type = 1 ORDER BY sort LIMIT 1) AS path
ORDER BY a.prodRegDate DESC ORDER BY (SELECT COUNT(*) FROM review WHERE a.prodSeq = product_prodSeq AND reviDelFlag = 0) DESC ORDER BY b.infoPrice * (100 - infoDiscount) / 100 ORDER BY b.infoPrice * (100 - infoDiscount) / 100 DESC
limit #{rowNumToShow} offset #{startRnumForMysql}
